function initSearch(){const n=document.getElementById("search-input"),e=document.getElementById("search-results");let t=[];fetch("/index.json").then(e=>e.json()).then(e=>{t=e}),n.addEventListener("input",n=>{const o=n.target.value.toLowerCase();if(o.length<2){e.innerHTML="";return}const i=t.filter(e=>e.title.toLowerCase().includes(o)||e.content.toLowerCase().includes(o));s(i)});function s(t){if(t.length===0){e.innerHTML="<p>Tidak ada hasil yang ditemukan.</p>";return}const n=t.map(e=>`
                <div class="search-result post-item">
                    <h2><a href="${e.permalink}">${e.title}</a></h2>
                    <div class="post-meta">
                        <time datetime="${e.date}">${new Date(e.date).toLocaleDateString("id-ID",{day:"2-digit",month:"short",year:"numeric"})}</time>
                        ${e.tags&&e.tags.length>0?`
                        <span class="tags">
                            ${e.tags.map(e=>`<span class="tag"><a href="/tags/${e}">#${e}</a></span>`).join(" ")}
                        </span>
                        `:""}
                    </div>
                </div>
            `).join("");e.innerHTML="<h2>Search Result</h2>"+n}}document.addEventListener("DOMContentLoaded",initSearch)